    def draw(self,game, gap, gap_y):
        self.screen = game.screen
        self.image = pygame.image.load('images/brick_wall25.png')
        self.tv1 = pygame.image.load('images/TV1_2.png') 
        self.rect = self.image.get_rect()
        #print("gap=", gap)
        for row in self.world_map_left_to_right:
            for x,y in row:
                #self.rect.x = x + TILE*gap
                #self.rect.y = y
                    #self.screen.blit(self.image, self.rect)
                pygame.draw.rect(self.screen, DARKGRAY, (x - TILE*gap, y - gap_y, TILE, TILE),2)
                
                
        for row in self.hard_wall:
            for xy in row:
                self.rect.x = xy[1] - TILE*gap
                self.rect.y = xy[0] - gap_y
                #print(f'x = {self.rect.x} y = {self.rect.y}')
                self.screen.blit(self.image, self.rect)
                #pygame.draw.rect(self.screen, RED, (row[1] - TILE*gap, row[0] - gap_y, TILE, TILE),2)
        
        for xy in self.tvs:
            self.rect.x = xy[1] - TILE*gap
            self.rect.y = xy[0] - gap_y
            #print(f'TV= {i}')
            self.screen.blit(self.tv1, self.rect)
        
