    def level_2_world_map(self):
        self.space = 0
        self.hard_wall = []
        self.aid_list = []
        self.world_map_list = []
        self.level_wight = len(self.text_map2)
        self.level_height = len(self.text_map2[0])
        print(f'self.level_wight = {self.level_wight} self.level_height = {self.level_height}')
        for j in range(len(self.text_map2)):
            temp = []
            temp_hard_wall = []
            for i in range(len(self.text_map2[j])):
                self.space +=1
                if self.text_map2[j][i] == 'W':
                    temp.append((i * TILE,j * TILE))
                elif self.text_map2[j][i] == 'X':
                    temp_hard_wall.append((i * TILE,j * TILE))
                elif self.text_map2[j][i] == 'A':
                    self.aid_list.append((i * TILE,j * TILE))
                elif self.text_map2[j][i] == 'T':
                    self.tvs.append((i * TILE,j * TILE))
            #temp = tuple(temp)
            self.world_map_list.append(temp)
            self.hard_wall.append(temp_hard_wall)
        #self.world_map_list = tuple(self.world_map_list)
        self.world_map = self.world_map_list
        
        

        string_of_array = []
        
        for i in range(len(self.text_map2[0])):
            temp = []
            for k in range(len(self.text_map2)):
                if self.text_map2[k][i] == 'W':
                    temp.append((k * TILE, i * TILE))
            #temp = tuple(temp)
            string_of_array.append(temp)
        #string_of_array = string_of_array#tuple(string_of_array)
        self.world_map_left_to_right = string_of_array
        
        
        string_of_array = []
        for i in range(len(self.text_map2[0])):
            temp = []
            for k in range(len(self.text_map2)):
                if self.text_map2[k][i] == 'X':
                    temp.append((k * TILE, i * TILE))
            #temp = tuple(temp)
            string_of_array.append(temp)
        self.hard_wall_left_to_right = string_of_array 
        
        #print(dir(self.world_map_left_to_right))
    
   
